var forge = require('../..')

var seed = ''

function rsaSpeed(seed) {

  const seedHex = Buffer.from(seed).toString('hex')
  const prng = forge.random.createInstance()
  prng.seedFileSync = () => seedHex

  console.time('generateKeyPair')
  forge.pki.rsa.generateKeyPair({bits: 4096, workers: 0, prng})
  console.timeEnd('generateKeyPair')
  console.log()
}
console.log('Running...')

rsaSpeed(seed)
